import React, { useEffect, useRef, useState } from "react";
import * as d3 from "d3";

import { getYear } from "../services/cloud_data_functions";

import "../css/D3YearGrid.css";

function D3YearGrid(props) {
  const d3Grid = useRef();
  const [data, setData] = useState([]);
  // 1 = day_number
  // 2 = radiation/c_index
  // 3 = months
  const [sortMethod, setSortMethod] = useState(1);
  const [svg, setSvg] = useState(null);
  const [recs, setRecs] = useState({});
  const [nRecsPerRow, setNRecsPerRow] = useState(null);
  const [gridGap, setGridGap] = useState({});

  useEffect(() => {
    // The data should be all ready when we load this component :)
    // console.log("Inside D3YearGrid!");
    // console.log(props.data);
    // console.log("year: " + props.year);

    const margin = { top: 0, right: 0, bottom: 0, left: 0 };
    const width = parseInt(d3.select(".d3Container").style("width"));
    // const height = parseInt(d3.select(".d3Container").style("height"));
    const height = 1 * width;
    const gridGapX = 0;
    const gridGapY = 0;
    setGridGap({ x: gridGapX, y: gridGapY });

    // set up the svg
    const svg_temp = d3
      .select(d3Grid.current)
      .attr("width", width - margin.left - margin.right)
      .attr("height", height - margin.top - margin.bottom)
      .attr("class", "D3Svg")
      // .append("g")
      .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

    setSvg(svg_temp);

    var svg_width = width;
    var n_recs_per_row = 20;
    var rec_w = Math.floor((0.7 * svg_width) / n_recs_per_row);
    var rec_h = rec_w;
    setNRecsPerRow(n_recs_per_row);
    setRecs({ width: rec_w, height: rec_h });

    // Add the rectangles
    svg_temp
      .selectAll("rect") // There's no 'rect' to select. But this is how we do to fill the svg with 'rects'.
      .data(props.data) // here we load the data
      .enter()
      .append("rect") // .enter().append() -> for each datapoint in the dataset, add a 'rect'.
      .attr("width", rec_w)
      .attr("height", rec_h)
      .attr("x", function (d, i) {
        return (i % n_recs_per_row) * (rec_w + gridGapX) + 0.15 * svg_width;
      })
      .attr("y", function (d, i) {
        return (
          Math.floor(i / n_recs_per_row) * (rec_h + gridGapY) + 0.15 * svg_width
        );
      })
      .attr("fill", function (d, i) {
        var bright_color = [100, 146, 230];
        var dark_color = [99, 112, 124];

        var factor = Math.floor(d.c_index / 10) * 10;

        // hard coding colors:
        var color = [];
        if (factor < 10) {
          // 1.jpg
          color = [100, 146, 230];
        } else if (factor >= 10 && factor < 20) {
          // 2.jpg
          color = [99, 143, 224];
        } else if (factor >= 20 && factor < 30) {
          // 3.jpg
          color = [96, 142, 214];
        } else if (factor >= 30 && factor < 40) {
          // 4.jpg
          color = [94, 138, 204];
        } else if (factor >= 40 && factor < 50) {
          // 5.jpg
          color = [94, 136, 193];
        } else if (factor >= 50 && factor < 60) {
          // 6.jpg
          color = [93, 131, 182];
        } else if (factor >= 60 && factor < 70) {
          // 7.jpg
          color = [93, 128, 170];
        } else if (factor >= 70 && factor < 80) {
          // 8.jpg
          color = [95, 124, 154];
        } else if (factor >= 80 && factor < 90) {
          // 9.jpg
          color = [97, 118, 138];
        } else if (factor >= 90) {
          // 10.jpg
          color = [99, 112, 124];
        }
        return "rgba(" + color[0] + "," + color[1] + "," + color[2] + ", 1)";
      })
      .html(function (d, i) {
        return d.day_number;
      });
  }, []);

  function handleSortLight() {
    // console.log("Sort Light");

    if (sortMethod !== 2) {
      // If the setState already dumped the dvg data into our svg variable
      if (svg) {
        let svg_width = svg.style("width").replace("px", "");

        svg
          .selectAll("rect")
          .sort(function (x, y) {
            return d3.ascending(x.c_index, y.c_index);
          })
          .transition()
          .duration(2000)
          .attr("width", recs.width)
          .attr("height", recs.height)
          .attr("x", function (d, i) {
            return (
              (i % nRecsPerRow) * (recs.width + gridGap.x) + 0.15 * svg_width
            );
          })
          .attr("y", function (d, i) {
            // console.log(Math.floor(i / 33));
            return (
              Math.floor(i / nRecsPerRow) * (recs.height + gridGap.y) +
              0.15 * svg_width
            );
          });
      }

      setSortMethod(2);
    }
  }

  function handleSortDay() {
    // console.log("Sort Day");

    if (sortMethod !== 1) {
      // If the setState already dumped the dvg data into our svg variable
      if (svg) {
        let svg_width = svg.style("width").replace("px", "");

        svg
          .selectAll("rect")
          .sort(function (x, y) {
            return d3.ascending(x.day_number, y.day_number);
          })
          .transition()
          .duration(2000)
          .attr("width", recs.width)
          .attr("height", recs.height)
          .attr("x", function (d, i) {
            return (
              (i % nRecsPerRow) * (recs.width + gridGap.x) + 0.15 * svg_width
            );
          })
          .attr("y", function (d, i) {
            // console.log(Math.floor(i / 33));
            return (
              Math.floor(i / nRecsPerRow) * (recs.height + gridGap.y) +
              0.15 * svg_width
            );
          });

        setSortMethod(1);
      }
    }
  }

  function handleSortMonth() {
    console.log("Sort Month");

    if (sort_method != 3) {
      console.log("Group Month!");

      var rectangles2 = svg.selectAll("rect");

      rectangles2
        .sort(function (x, y) {
          return d3.ascending(x.day_number, y.day_number);
        })
        .transition()
        .duration(2000)
        .attr("width", rec_w)
        .attr("height", rec_h)
        .attr("x", function (d, i) {
          // January
          if (d.month == 1) {
            return (i % 6) * rec_w + 0.05 * svg_width;
          }

          // February
          else if (d.month == 2) {
            return ((i - 31) % 6) * rec_w + 0.05 * svg_width + 7 * rec_w;
          }

          // March
          else if (d.month == 3) {
            return ((i - 59) % 6) * rec_w + 0.05 * svg_width + 14 * rec_w;
          }

          // April
          else if (d.month == 4) {
            return ((i - 90) % 6) * rec_w + 0.05 * svg_width + 21 * rec_w;
          }

          // May
          else if (d.month == 5) {
            return ((i - 120) % 6) * rec_w + 0.05 * svg_width;
          }

          // Jun
          else if (d.month == 6) {
            return ((i - 151) % 6) * rec_w + 0.05 * svg_width + 7 * rec_w;
          }

          // July
          else if (d.month == 7) {
            return ((i - 181) % 6) * rec_w + 0.05 * svg_width + 14 * rec_w;
          }

          // August
          else if (d.month == 8) {
            return ((i - 212) % 6) * rec_w + 0.05 * svg_width + 21 * rec_w;
          }

          // September
          else if (d.month == 9) {
            return ((i - 243) % 6) * rec_w + 0.05 * svg_width;
          }

          // October
          else if (d.month == 10) {
            return ((i - 273) % 6) * rec_w + 0.05 * svg_width + 7 * rec_w;
          }

          // November
          else if (d.month == 11) {
            return ((i - 304) % 6) * rec_w + 0.05 * svg_width + 14 * rec_w;
          }

          // December
          else if (d.month == 12) {
            return ((i - 334) % 6) * rec_w + 0.05 * svg_width + 21 * rec_w;
          } else {
            return 500;
          }
        })
        .attr("y", function (d, i) {
          // January
          if (d.month == 1) {
            return Math.floor(i / 6) * rec_h + 0.05 * svg_width;
          }

          // February
          else if (d.month == 2) {
            return Math.floor((i - 31) / 6) * rec_h + 0.05 * svg_width;
          }

          // March
          else if (d.month == 3) {
            return Math.floor((i - 59) / 6) * rec_h + 0.05 * svg_width;
          }

          // April
          else if (d.month == 4) {
            return Math.floor((i - 90) / 6) * rec_h + 0.05 * svg_width;
          }

          // May
          else if (d.month == 5) {
            return (
              Math.floor((i - 120) / 6) * rec_h + 0.05 * svg_width + 9 * rec_h
            );
          }

          // June
          else if (d.month == 6) {
            return (
              Math.floor((i - 151) / 6) * rec_h + 0.05 * svg_width + 9 * rec_h
            );
          }

          // July
          else if (d.month == 7) {
            return (
              Math.floor((i - 181) / 6) * rec_h + 0.05 * svg_width + 9 * rec_h
            );
          }

          // August
          else if (d.month == 8) {
            return (
              Math.floor((i - 212) / 6) * rec_h + 0.05 * svg_width + 9 * rec_h
            );
          }

          // September
          else if (d.month == 9) {
            return (
              Math.floor((i - 243) / 6) * rec_h + 0.05 * svg_width + 18 * rec_h
            );
          }

          // October
          else if (d.month == 10) {
            return (
              Math.floor((i - 273) / 6) * rec_h + 0.05 * svg_width + 18 * rec_h
            );
          }

          // November
          else if (d.month == 11) {
            return (
              Math.floor((i - 304) / 6) * rec_h + 0.05 * svg_width + 18 * rec_h
            );
          }

          // Dececmber
          else if (d.month == 12) {
            return (
              Math.floor((i - 334) / 6) * rec_h + 0.05 * svg_width + 18 * rec_h
            );
          }
        });

      // Writing months names
      var month_labels = svg.selectAll("text");

      month_labels
        .transition()
        .duration(2000)
        .attr("x", function (d, i) {
          if (d === "January") {
            return 0.05 * svg_width;
          } else if (d === "February") {
            return 0.05 * svg_width + 7 * rec_w;
          } else if (d === "March") {
            return 0.05 * svg_width + 14 * rec_w;
          } else if (d === "April") {
            return 0.05 * svg_width + 21 * rec_w;
          }
          // 2nd row
          else if (d === "May") {
            return 0.05 * svg_width;
          } else if (d === "June") {
            return 0.05 * svg_width + 7 * rec_w;
          } else if (d === "July") {
            return 0.05 * svg_width + 14 * rec_w;
          } else if (d === "August") {
            return 0.05 * svg_width + 21 * rec_w;
          }
          // 3rd row
          else if (d === "September") {
            return 0.05 * svg_width;
          } else if (d === "October") {
            return 0.05 * svg_width + 7 * rec_w;
          } else if (d === "November") {
            return 0.05 * svg_width + 14 * rec_w;
          } else if (d === "December") {
            return 0.05 * svg_width + 21 * rec_w;
          }
        })
        .attr("y", function (d, i) {
          if (d === "January") {
            return 0.05 * svg_width - 10;
          } else if (d === "February") {
            return 0.05 * svg_width - 10;
          } else if (d === "March") {
            return 0.05 * svg_width - 10;
          } else if (d === "April") {
            return 0.05 * svg_width - 10;
          }

          // 2nd row
          else if (d === "May") {
            return 0.05 * svg_width + 9 * rec_h - 10;
          } else if (d === "June") {
            return 0.05 * svg_width + 9 * rec_h - 10;
          } else if (d === "July") {
            return 0.05 * svg_width + 9 * rec_h - 10;
          } else if (d === "August") {
            return 0.05 * svg_width + 9 * rec_h - 10;
          }
          // 3rd row
          else if (d === "September") {
            return 0.05 * svg_width + 18 * rec_h - 10;
          } else if (d === "October") {
            return 0.05 * svg_width + 18 * rec_h - 10;
          } else if (d === "November") {
            return 0.05 * svg_width + 18 * rec_h - 10;
          } else if (d === "December") {
            return 0.05 * svg_width + 18 * rec_h - 10;
          }
        });
      setSortMethod(3);
    }
  }

  function handleSlideChange(event) {
    // console.log("Slide changed: " + event.target.value);
  }

  return (
    <React.Fragment>
      <div className="grid_controlers">
        <button onClick={handleSortLight}>Sort Light</button>
        <button onClick={handleSortDay}>Sort Date</button>
        <button onClick={handleSortMonth}>Group Months</button>

        <div className="slidecontainer">
          <input
            type="range"
            min="0"
            max="100"
            defaultValue="100"
            className="slider"
            id="colorSlider"
            onInput={handleSlideChange}
          />
          <p id="slide_value_p"> 100</p>
          <div id="color_sample"></div>
        </div>
      </div>

      <div className="d3Container">
        <svg ref={d3Grid}></svg>
      </div>
    </React.Fragment>
  );
}

export default D3YearGrid;
