import React, { useState, useEffect } from "react";
import ADaySkyPhotoSmall from "./aDaySkyPhotoSmall";
import "../css/yearGridSmall.css";

import { getYear } from "../services/years_data_functions";

function YearGridSmall(props) {
  const [yearsData, setYearsData] = useState(null);
  const [test, setTest] = useState(0);

  useEffect(() => {
    // This function is executed every time the component is rendered.
    // So it's the same as calling both:
    // componentDidMount -> the first time the component is mounted.
    // componentDidUpdate -> when receives new props or the state changes.

    // console.log("props.year: " + props.year);
    // setYearsData(getYear(props.year));
    // console.log(yearsData);

    console.log("test: " + test);
    setTest(97);
    console.log("test: " + test);
  }, [test]); // this array that is passed as the second parameter of the useEffect function indicates which variables are the triggers to call useEffect. If these variables change, the useEffect will be called. If we don't have this array, the function will be called on any props/state change.

  return (
    <div className="yearGridSmall">
      {Array.from(Array(365).keys()).map((i) => (
        <ADaySkyPhotoSmall key={i} year={props.year} />
      ))}
    </div>
  );
}

export default YearGridSmall;
