import React, { Fragment, useState, useEffect } from "react";
import YearGridSmall from "./yearGridSmall";
import "../css/yearPanelSmall.css";
import D3YearGrid from "./D3YearGrid";
import { getYear } from "../services/cloud_data_functions";

function YearPanelSmall(props) {
  const [data, setData] = useState([]);

  useEffect(() => {
    // // We need to query the data asyncronously because it might take a while to receive the data.
    // let getDataFunction = async () => {
    //   return getYear(props.year);
    // };
    // // When we are done getting the data, the function inside ".then()" is executed.
    // getDataFunction().then((values) => {
    //   // Saving the data.
    //   setData(values);
    // });
    setData(getYear(props.year));
  }, []);

  function handleSortLight() {
    console.log("Sort Light");
  }

  function handleSortDay() {
    console.log("Sort Day");
  }

  function handleSortMonth() {
    console.log("Sort Day");
  }

  function handleSlideChange(event) {
    // console.log("Slide changed: " + event.target.value);
  }

  return (
    <div className="yearPanelSmallDiv">
      <div className="grid_controlers">
        <button onClick={handleSortLight}>Sort Light</button>
        <button onClick={handleSortDay}>Sort Date</button>
        <button onClick={handleSortMonth}>Group Months</button>

        <div className="slidecontainer">
          <input
            type="range"
            min="0"
            max="100"
            defaultValue="100"
            className="slider"
            id="colorSlider"
            onInput={handleSlideChange}
          />
          <p id="slide_value_p"> 100</p>
          <div id="color_sample"></div>
        </div>
      </div>

      <div className="header">
        <h4 className="year">{props.year}</h4>
        <button> x </button>
        <button> > </button>
      </div>

      <div>
        {/* <YearGridSmall year={props.year} /> */}
        <ul>{data.map((d) => "<p>oi</p>")}</ul>
        {/* <D3YearGrid data={data} /> */}
      </div>
    </div>
  );
}

export default YearPanelSmall;
